import React from 'react'
import {list, ListItem, ListItemAvatar, Avatar, ListItemText, Button, Modal} from '@material-ui/core';
// import './Todo.css';
import db from './firebase';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles } from '@material-ui/core/styles';
import {useState} from 'react'
import EditIcon from '@material-ui/icons/Edit';

const useStyles = makeStyles((theme) => ({
    paper: {
      position: 'absolute',
      width: 400,
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));

function Todo(props) {
    const classes = useStyles();
    const [open, setOpen] = useState(false);
    const [input, setInput] = useState();
    const handleOpen = () => {
        setOpen=(true);
    };

    const updateTodo = () => {
        // update the todo with the new input text 
        db.collection('todos').doc(props.todo.todo).set({
            todo: input
        }, {merge: true}) // basically appends
        setOpen(false);
    }

    return (
        <>
        <Modal
        open={open}
        onClose={e => setOpen(false)}
        >
        <div className={classes.paper}>
            <h1>I am a Modal</h1>
            <input placeholder={props.todo.todo} value={input} onChnage={event => setInput(event.target.value)} />
            <Button onClick={updateTodo}>Edit Todo</Button>
        </div>
        </Modal>
        <list>
            <ListItem> 
                <ListItemAvatar>
                </ListItemAvatar>
                    <ListItemText primary={props.todo.todo} secondary="Dummy deadline">
                    </ListItemText>
                    <EditIcon variant="contained" color="primary" onClick={e => setOpen(true)}>Edit</EditIcon>
                    <DeleteIcon variant="contained" color="secondary" onClick={event => db.collection('todos').doc(props.todo.id).delete()}>DELETE</DeleteIcon>

            </ListItem>
        </list>
        </>
    )
}

export default Todo
