import {useState} from 'react';
import './App.css';
import {Button, FormControl, Input, InputLabel} from '@material-ui/core';
import Todo from './Todo';

function App() {
  //State for todos, which we later map to list it out
  const [todos, setTodos] = useState(['Take dogs for a walk', 'Take the trash out', 'order takeout']); 

  //State for input
  const [input, setInput] = useState('');
  console.log(input);

  const addTodo = (event) => {
    //this will trigger the add todo button

    event.preventDefault(); //prevents refreshing page when submit

    console.log('im working');

    //using spread '...' appending or adding in the curent contents
    setTodos([...todos,input]); 

    setInput('');

  }
  return (
    <div className="App">
    <h1>Hello World!</h1>
    <form>
      <FormControl>
        <InputLabel>Write a Todo</InputLabel>
        <Input value={input} onChange={event => setInput(event.target.value)}/>
      </FormControl>

      <Button disabled={!input} type="submit" onClick={addTodo} Button variant="contained" color="primary">
          Add Todo
      </Button>
    </form>


    <ul>
      {todos.map(todo => (
        <li>{todo}</li>
      ))}
    </ul>
    </div>
  );
}

export default App;
